function showEvents(){document.querySelector(".mobile-menu").addEventListener("click",navegacionResponsive);document.querySelector(".nav__close").addEventListener("click",cierreMenu)}function navegacionResponsive(){document.querySelector(".menu__hidden").classList.toggle("show")}function cierreMenu(){document.querySelector(".menu__hidden").classList.remove("show")}function darkMode(){const e=window.matchMedia("prefers-color-scheme:dark");e.matches?document.body.classList.add("dark-mode"):document.body.classList.remove("dark-mode"),e.addEventListener("change",(function(){e.matches,document.body.classList.add("dark-mode")}));document.querySelector(".dark-mode-button").addEventListener("click",changeDarkMode)}function changeDarkMode(){document.body.classList.toggle("dark-mode")}document.addEventListener("DOMContentLoaded",(function(){showEvents(),darkMode()}));